import Swal from 'sweetalert2';
import * as i0 from "@angular/core";
export class MensajeService {
    constructor() {
    }
    success(titulo, texto) {
        Swal.fire(titulo, texto, 'success');
    }
    error(titulo, texto) {
        Swal.fire(titulo, texto, 'error');
    }
    warning(titulo, texto) {
        Swal.fire(titulo, texto, 'warning');
    }
    async questions(pregunta, titulo, texto) {
        let confirmacion = false;
        Swal.fire({
            title: pregunta,
            showDenyButton: true,
            showCancelButton: true,
            confirmButtonText: 'Guardar',
            denyButtonText: `Cancelar`,
        }).then((result) => {
            /* Read more about isConfirmed, isDenied below */
            if (result.isConfirmed) {
                Swal.fire(titulo, texto, 'success');
                confirmacion = true;
            }
            else if (result.isDenied) {
                Swal.fire('', 'No se realizo ningun cambio', 'info');
                confirmacion = false;
            }
        });
        return confirmacion;
    }
}
